import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Select, message } from 'antd';
import { getSubject, updateSubject } from './subjectService';
import { useParams, useNavigate } from 'react-router-dom';

const { Option } = Select;

const EditSubjectPage = () => {
    const [form] = Form.useForm();
    const { id } = useParams();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchSubject = async () => {
            setLoading(true);
            try {
                const response = await getSubject(id);
                form.setFieldsValue(response.data);
            } catch (error) {
                message.error('Failed to fetch subject details');
            } finally {
                setLoading(false);
            }
        };
        fetchSubject();
    }, [id, form]);

    const onFinish = async (values) => {
        setLoading(true);
        try {
            await updateSubject(id, values);
            message.success('Subject updated successfully');
            navigate('/subjects');
        } catch (error) {
            message.error('Failed to update subject');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <Form
                form={form}
                layout="vertical"
                onFinish={onFinish}
                autoComplete="off"
            >
                <Form.Item
                    name="subjectName"
                    label="Tên môn học"
                    rules={[{ required: true, message: 'Please input the subject name!' }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    name="subjectCode"
                    label="Mã môn học"
                    rules={[{ required: true, message: 'Please input the subject code!' }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    name="status"
                    label="Trạng thái"
                    rules={[{ required: true, message: 'Please select a status!' }]}
                >
                    <Select placeholder="Select a status">
                        <Option value="active">Hoạt động</Option>
                        <Option value="inactive">Không hoạt động</Option>
                    </Select>
                </Form.Item>
                <Form.Item>
                    <Button type="primary" htmlType="submit" loading={loading}>
                        Cập nhật
                    </Button>
                    <Button
                        style={{ margin: '0 8px' }}
                        onClick={() => form.resetFields()}
                        disabled={loading}
                    >
                        Hủy bỏ
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
};

export default EditSubjectPage;
