import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getSubject, createSubject, updateSubject } from './subjectService';
import { Form, Input, Button, message } from 'antd';

const SubjectSavePage = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [form] = Form.useForm();
    const isNewSubject = !id;

    useEffect(() => {
        if (!isNewSubject) {
            loadSubject();
        }
    }, [id]);

    const loadSubject = async () => {
        try {
            const { data } = await getSubject(id);
            form.setFieldsValue(data);
        } catch (error) {
            message.error('Failed to load the subject');
        }
    };

    const handleSubmit = async (values) => {
        try {
            if (isNewSubject) {
                await createSubject(values);
                message.success('Subject created successfully!');
            } else {
                await updateSubject(id, values);
                message.success('Subject updated successfully!');
            }
            navigate('/subjects');
        } catch (error) {
            message.error('Failed to save the subject');
        }
    };

    return (
        <div>
            <h1>{isNewSubject ? 'Create New Subject' : 'Edit Subject'}</h1>
            <Form form={form} onFinish={handleSubmit} layout="vertical">
                <Form.Item
                    name="subjectName"
                    label="Subject Name"
                    rules={[{ required: true, message: 'Please input the subject name!' }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item
                    name="subjectCode"
                    label="Subject Code"
                    rules={[{ required: true, message: 'Please input the subject code!' }]}
                >
                    <Input />
                </Form.Item>
                <Form.Item name="description" label="Description">
                    <Input.TextArea />
                </Form.Item>
                <Button type="primary" htmlType="submit">
                    {isNewSubject ? 'Create' : 'Update'}
                </Button>
            </Form>
        </div>
    );
};

export default SubjectSavePage;
